INSERT INTO books (title, publisher, author, ISBN, page_number, summary, book_language, keywords)
VALUES ('The Great Gatsby', 'Scribner', 'F. Scott Fitzgerald', '9780743273565', 180, 'The story primarily concerns the young and mysterious millionaire Jay Gatsby and his quixotic passion and obsession with the beautiful former debutante Daisy Buchanan. ', 'English', 'Classic, Romance'),
('To Kill a Mockingbird', 'J. B. Lippincott & Co.', 'Harper Lee', '9780061120084', 281, 'The story takes place during three years of the Great Depression in the fictional "tired old town" of Maycomb, Alabama.', 'English', 'Classic, Social justice'),
('Harry Potter and the Philosopher''s Stone', 'Bloomsbury Publishing', 'J.K. Rowling', '9780747532743', 223, 'Harry Potter has been living a miserable life with his Aunt Petunia and Uncle Vernon. ', 'English', 'Fantasy, Magic'),
('Pride and Prejudice', 'T. Egerton, Whitehall', 'Jane Austen', '9780141439518', 279, 'The novel follows the character development of Elizabeth Bennet, the dynamic protagonist of the book, who learns about the repercussions of hasty judgments and comes to appreciate the difference between superficial goodness and actual goodness.', 'English', 'Classic, Romance, Society'),
('The Catcher in the Rye', 'Little, Brown and Company', 'J.D. Salinger', '9780316769488', 224, 'The novel details two days in the life of 16-year-old Holden Caulfield after he has been expelled from prep school.', 'English', 'Coming-of-age, Society'),
('1984', 'Secker and Warburg', 'George Orwell', '9780451524935', 328, 'The story takes place in an imagined future, the year 1984, when much of the world has fallen victim to perpetual war, omnipresent government surveillance, historical negationism, and propaganda.', 'English', 'Dystopian, Political fiction'),
('Animal Farm', 'Secker and Warburg', 'George Orwell', '9780451526342', 112, 'The novel tells the story of a group of farm animals who rebel against their human farmer, hoping to create a society where the animals can be equal, free, and happy.', 'English', 'Political satire, Allegory'),
('Brave New World', 'Chatto & Windus', 'Aldous Huxley', '9780060850524', 288, 'The novel anticipates developments in reproductive technology, sleep-learning, psychological manipulation, and classical conditioning that are combined to make a dystopian society which is challenged by only a single individual: the storys protagonist.', 'English', 'Dystopian, Science fiction'),
('The Hobbit', 'George Allen & Unwin', 'J.R.R. Tolkien', '9780547928227', 310, 'The story is set in a world filled with magical creatures and tells the tale of a hobbit named Bilbo Baggins who is recruited by a wizard named Gandalf to help a group of dwarves reclaim their home and treasure from a dragon.', 'English', 'Fantasy, Adventure');

INSERT INTO books (title, publisher, author, ISBN, page_number, summary, book_language, keywords)
VALUES ('The Hunger Games', 'Scholastic Press', 'Suzanne Collins', '9780439023481', 374, 'The Hunger Games is set in a post-apocalyptic dystopian world where the Capitol, a wealthy and technologically advanced city, holds power over the rest of the nation. Each year, the Capitol holds the Hunger Games, a televised event where one boy and one girl from each of the 12 districts must fight to the death.', 'English', 'Dystopian, Action'),
('The Girl with the Dragon Tattoo', 'Norstedts Förlag', 'Stieg Larsson', '9780307269751', 600, 'The novel tells the story of Mikael Blomkvist, a journalist who is hired to investigate the disappearance of a wealthy businessman''s niece, and Lisbeth Salander, a brilliant but troubled computer hacker who helps him with the investigation.', 'Swedish', 'Mystery, Thriller'),
('The Da Vinci Code', 'Doubleday', 'Dan Brown', '9780385504201', 454, 'The novel follows symbologist Robert Langdon and cryptologist Sophie Neveu as they investigate a murder in the Louvre Museum in Paris, and discover a trail of clues that lead them to a secret society and a religious mystery that could shake the foundations of Christianity.', 'English', 'Mystery, Thriller'),
('The Alchemist', 'HarperCollins', 'Paulo Coelho', '9780061122415', 208, 'The Alchemist follows the journey of an Andalusian shepherd boy named Santiago who dreams of discovering a treasure hidden in the Egyptian pyramids. Along the way, he meets a series of characters who teach him important lessons about life and love.', 'English', 'Fable, Quest'),
('The Fault in Our Stars', 'Dutton Books', 'John Green', '9780525478812', 313, 'The Fault in Our Stars is a love story between two teenagers with cancer, Hazel and Gus. Despite the challenges they face, they find love and hope in each other, and embark on a journey to Amsterdam to meet their favorite author.', 'English', 'Romance, Drama'),
('The Chronicles of Narnia', 'Geoffrey Bles', 'C.S. Lewis', '9780007117307', 767, 'The Chronicles of Narnia is a series of seven fantasy novels set in the fictional world of Narnia. The books tell the story of a group of children who discover a magical wardrobe that leads them to a world filled with talking animals, mythical creatures, and epic battles between good and evil.', 'English', 'Fantasy, Adventure'),
('The Picture of Dorian Gray', 'Lippincott''s Monthly Magazine', 'Oscar Wilde', '9780486278070', 254, 'The novel tells the story of a young man named Dorian Gray who becomes obsessed with his own beauty and youth. He sells his soul to the devil in exchange for eternal youth, and his portrait ages instead of him. As he becomes more and more corrupt, the portrait becomes more and more grotesque.', 'English', 'Gothic, Horror');

INSERT INTO books (title, publisher, author, ISBN, page_number, summary, book_language, keywords)
VALUES ('The Scapegoat', 'Ypsilon', 'Sophia Nikolaidou', '9789601425009', 384, 'The Scapegoat is a contemporary novel set in Athens during the Greek economic crisis. The story follows the lives of three characters whose paths intersect as they struggle to survive in a society that is collapsing around them.', 'Greek', 'Contemporary, Economic Crisis'),
('Austerity Measures', 'Serpent''s Tail', 'Yanis Varoufakis', '9781846689953', 320, 'Austerity Measures is a collection of essays by Yanis Varoufakis, the former finance minister of Greece. The book offers a critical analysis of the European Union''s response to the Greek debt crisis, and argues for an alternative approach to economics and politics.', 'Greek', 'Politics, Economics'),
('The Last Temptation', 'Faber & Faber', 'Nikos Kazantzakis', '9780571321715', 608, 'The Last Temptation is a novel that reimagines the life of Jesus Christ. The book explores themes of faith, doubt, and the nature of sacrifice, and caused controversy upon its publication due to its controversial portrayal of Christ.', 'Greek', 'Fiction, Religion'),
('Christ Recrucified', 'Faber & Faber', 'Nikos Kazantzakis', '9780571321708', 496, 'Christ Recrucified is a novel that tells the story of a small Greek village that stages a Passion Play. The book explores themes of faith, sacrifice, and the human condition, and is considered one of Kazantzakis''s masterpieces.', 'Greek', 'Fiction, Religion'),
('The Greek War of Independence: The Struggle for Freedom from Ottoman Oppression', 'Open Road Media', 'David Brewer', '9781480437678', 576, 'The Greek War of Independence is a comprehensive history of the Greek Revolution against Ottoman rule in the early 19th century. The book explores the political, social, and cultural factors that led to the rebellion, and the impact it had on Greece and the wider world.', 'English', 'History, Greece');

