INSERT INTO books (title, publisher, author, ISBN, page_number, summary, book_language, keywords)
VALUES ('The Leopard', 'Vintage Crime/Black Lizard', 'Jo Nesbø', '9780099546790', 640, 'The Leopard is the eighth novel featuring Harry Hole, and involves the investigation of a series of murders that are linked to a notorious Norwegian serial killer. The novel was praised for its suspenseful plot and detailed characterization of Harry.', 'English', 'Crime fiction, Detective, Norway'),
('Police', 'Vintage Crime/Black Lizard', 'Jo Nesbø', '9780307960498', 640, 'Police is the tenth novel featuring Harry Hole, and deals with a series of killings that appear to be linked to a case that Harry investigated years ago. The novel was praised for its dark and twisty plot and the return of some beloved characters from the series.', 'English', 'Crime fiction, Detective, Norway'),
('The Son', 'Vintage Crime/Black Lizard', 'Jo Nesbø', '9780307960474', 400, 'The Son is a standalone novel by Jo Nesbø that follows the story of a young man who is serving time in prison for crimes he did not commit. When he is released, he sets out to find the people who set him up and clear his name. The novel was praised for its suspenseful plot and its exploration of themes like justice and revenge.', 'English', 'Suspense fiction, Norway'),
('Headhunters', 'Vintage Crime/Black Lizard', 'Jo Nesbø', '9780307948687', 288, 'Headhunters is a standalone novel by Jo Nesbø that follows the story of Roger Brown, a successful headhunter who is also an art thief. When he tries to steal a valuable painting from one of his clients, he gets more than he bargained for. The novel was praised for its fast-paced plot and its memorable protagonist.', 'English', 'Suspense fiction, Art theft, Norway'),
