INSERT INTO books (title, publisher, author, ISBN, page_number, summary, book_language, keywords)
VALUES ('Z', 'Kedros', 'Vassilis Vassilikos', '9789600432437', 250, 'Z is a political novel set in Greece during the 1960s and follows the investigation of a murder of a left-wing activist. The novel explores themes of political corruption and the abuse of power. It was adapted into an Oscar-nominated film directed by Costa-Gavras.', 'Greek', 'Political fiction, Historical fiction, Film adaptation'),
('The Island', 'Kedros', 'Victoria Hislop', '9789600432895', 472, 'The Island is a novel set on the Greek island of Spinalonga, which was once a leper colony. The novel follows the story of a young woman who discovers her familys connection to the island and its inhabitants. The novel has been translated into over 25 languages and was adapted into a television series.', 'Greek', 'Historical fiction, Romance, Family saga, Television adaptation'),
('The Return', 'Kedros', 'Dimitris Papadimitriou', '9789600419247', 265, 'The Return is a novel that explores the lives of a group of Greek immigrants living in Germany. The novel follows the story of a young woman who returns to Greece to search for her missing father. The novel deals with themes of identity, culture, and the immigrant experience.', 'Greek', 'Contemporary fiction, Immigrant literature'),
