INSERT INTO books (title, publisher, author, ISBN, page_number, summary, book_language, keywords)
VALUES ('Percy Jackson and The Lightning Thief', 'Disney Hyperion', 'Rick Riordan', '9781423106977', 375, 'Percy Jackson and The Lightning Thief is a fantasy adventure novel about a young boy named Percy who discovers he is the son of Poseidon, the Greek god of the sea. He goes on a quest to retrieve Zeus''s stolen lightning bolt and clear his name. The novel deals with themes of Greek mythology, friendship, and bravery.', 'English', 'Children''s literature, Fantasy, Adventure, Greek mythology'),
